const cardContainer=document.getElementById("card-container"),loading=document.getElementById("loading");let page=1;const loadCards=async()=>{try{const t=await fetch(`https://picsum.photos/v2/list?page=${page}&limit=6`);(await t.json()).forEach((t=>{const e=document.createElement("div");e.classList.add("col-md-6","mb-4"),e.innerHTML=`\n                <div class="card">\n                  <img\n                    src="${t.download_url}"\n                    class="card-img-top"\n                    alt="${t.author}"\n                  />\n                  <div class="card-body">\n                    <h5 class="card-title">${t.author}</h5>\n                    <p class="card-text">${t.width} x ${t.height}</p>\n                  </div>\n                  <div class="card-footer py-4">\n                    <a href="#" style="--djn-btn-color: #fff" class="btn btn-primary me-3">Save to collection</a>\n                    <a href="#" class="btn btn-outline-dark">Share</a>\n                  </div>\n                </div>\n      `,cardContainer.appendChild(e)})),page++}catch(t){console.error(t)}},handleScroll=()=>{const{scrollTop:t,scrollHeight:e,clientHeight:n}=document.documentElement;t+n>=e-5&&(loading.classList.add("show"),setTimeout((()=>{loading.classList.remove("show"),loadCards()}),500))};window.addEventListener("scroll",handleScroll),loadCards();const checkbox=document.getElementById("darkLightSwitch");checkbox.addEventListener("change",(function(){this.checked?document.documentElement.setAttribute("data-bs-theme","light"):document.documentElement.setAttribute("data-bs-theme","dark")}));