const cardContainer=document.getElementById("card-container"),loading=document.getElementById("loading");let page=1;const textPieces=["Lorem ipsum dolor sit amet.","Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae; Duis pulvinar dolor quis lorem vehicula blandit.","Maecenas hendrerit enim eu neque bibendum, vel dictum neque scelerisque.","Suspendisse nec lacus euismod, sollicitudin purus vel, consequat turpis.","Nulla facilisi. Phasellus lacinia semper est, a lacinia felis ultricies eu."];function getRandomText(){return textPieces[Math.floor(Math.random()*textPieces.length)]}const loadCards=async()=>{try{const e=await fetch(`https://picsum.photos/v2/list?page=${page}&limit=6`);(await e.json()).forEach((async e=>{const t=getRandomText(),n=document.createElement("div");n.classList.add("col-md-6","mb-4"),n.innerHTML=`\n                <div class="card">\n                  <img\n                    src="${e.download_url}"\n                    class="card-img-top"\n                    alt="${e.author}"\n                  />\n                  <div class="card-body">\n                    <h5 class="card-title">${e.author}</h5>\n                    <p class="card-text two-line-ellipsis" style="min-height: 48px">${t}</p>\n                  </div>\n                  <div class="card-footer py-4">\n                    <a href="#" style="--djn-btn-color: #fff" class="btn btn-primary me-3">Save to collection</a>\n                    <a href="#" class="btn btn-outline-dark">Share</a>\n                  </div>\n                </div>\n      `,cardContainer.appendChild(n)})),page++}catch(e){console.error(e)}},handleScroll=()=>{const{scrollTop:e,scrollHeight:t,clientHeight:n}=document.documentElement;e+n>=t-5&&(loading.classList.add("show"),setTimeout((()=>{loading.classList.remove("show"),loadCards()}),500))};window.addEventListener("scroll",handleScroll),loadCards();const checkbox=document.getElementById("darkLightSwitch");checkbox.addEventLi